import os

base = "_delay-0.04s.xpm"

for i in range(2,62):
	if i < 10:
		file = f"frame_0{i}{base}"
	else:
		file = f"frame_{i}{base}"
	with open(f"frames/{file}", "r") as f:
		lines = f.readlines()
		for i in range(1253, 1282):
			lines[i] = "\"\",\n"
	with open(f"frames2/{file}", "w") as f2:
		f2.writelines(lines)